import { Component, Injectable, OnInit } from '@angular/core';
import { Product } from './interfaces/product';
import { ProductviewService } from './services/productview.service';
import { MatCarousel, MatCarouselComponent } from '@ngmodule/material-carousel';

interface Food {
  value: string;
  viewValue: string;
}

@Component({
  selector: 'app-shopview',
  templateUrl: './shopview.component.html',
  styleUrls: ['./shopview.component.css']
})

@Injectable()
export class ShopviewComponent implements OnInit {

  activeIndex: number = 0;

  errorMessage: String;
  productList: Product[];
  products: Product[];
  sortOrder: number;
  sortField: string;

  typeList: Product[] = [];
  productOnfilter: Product[] = [];
  hotProduct: Product[] = [];
  hotProductList: Product[] = [];
  // foods: Food[] = [
  //   { value: 'steak-0', viewValue: 'Steak' },
  //   { value: 'pizza-1', viewValue: 'Pizza' },
  //   { value: 'tacos-2', viewValue: 'Tacos' }
  // ];
  slides = [{ 'image': 'https://i1.wp.com/act.wu.ac.th/wp-content/uploads/2020/03/73482706_113571776725263_4748642144560873472_n.jpg?fit=300%2C229&ssl=1' },
  { 'image': 'data:image/jpeg;base64,' },
  { 'image': 'https://scontent.fbkk10-1.fna.fbcdn.net/v/t1.0-9/131952883_412520580163713_3641001711615465085_n.jpg?_nc_cat=109&ccb=3&_nc_sid=8bfeb9&_nc_eui2=AeGCXdLbSeCPGKnpyj-taHbZ5eqsFh4mWiPl6qwWHiZaIzhcJVB9Zbl2zC1PbWOLnSm48Ov2c11v5fYb2MyR2jF7&_nc_ohc=XDpYBOPsEaIAX8BoSE2&_nc_ht=scontent.fbkk10-1.fna&oh=75df233f8c28659428559077fdccf091&oe=60663EE2' },
  { 'image': 'data:image/jpeg;base64,' }];
  constructor(
    private productViewService: ProductviewService
  ) { }

  ngOnInit() {

    this.productViewService.getProductType().subscribe(
      res => {
        this.typeList = res;
        this.productViewService.getProduct().subscribe(
          res => {
            this.productList = res;
            this.onClickSelectType(0)
          },
          error => this.errorMessage = <any>error
        )
      }
    )
    this.productViewService.getHotProducte().subscribe(
      res => {
        this.hotProduct = res;
        this.hotProduct.sort(function (a, b) {
          return a.count - b.count;
        });
        this.hotProductList = this.hotProduct;
        // this.sortProduct();
      }
    )
  }



  // sortProduct() {
  //   this.productList.sort(function (a, b) {
  //     return a.count - b.count;
  //   });
  //   this.hotProductList = this.productList;
  // }



  onClickSelectType(index) {
    this.productOnfilter = this.productList.filter((element) => this.filterByType(element, index));
    console.log(this.productOnfilter)
  }

  filterByType(element, index) {
    return (element.product_type == this.typeList[index].product_type)
  }

  onSortChange(event) {
    let value = event.value;
    if (value.indexOf('!') === 0) {
      this.sortOrder = -1;
      this.sortField = value.substring(1, value.length);
    }
    else {
      this.sortOrder = 1;
      this.sortField = value;
    }
  }

}
